import React, { useState } from "react";

export default function TextForm(props) {
  // const handleClearclick = () => {
  //   console.log("Clear was clicked");
  //   let newText = " ";
  //   setText(newText);
  // };

  const handleUpperCase = () => {
    console.log("Uppercase was clicked");
    let newText = text.toUpperCase();
    setText(newText);
  };

  const handleLowerCase = () => {
    console.log("Lowercase was clicked");
    let newText = text.toLowerCase();
    setText(newText);
  };

  const handleSentenseCase = () => {
    console.log("Sentensecase was clicked");
    let strSplit = text.toLowerCase().split(" ");
    for (let i = 0; i < strSplit.length; i++) {
      if (i === 0) {
        strSplit[i] =
          strSplit[i].charAt(i).toUpperCase() + strSplit[i].slice(1);
      } else {
        strSplit[i] = strSplit[i].toLowerCase();
      }
    }
    let newText = strSplit.join(" ");
    setText(newText);
  };
  const handleTitleCase = () => {
    console.log("Capitalizecase was clicked");
    let strSplit = text.toLowerCase().split(" ");
    for (let i = 0; i < strSplit.length; i++) {
      strSplit[i] = strSplit[i].charAt(0).toUpperCase() + strSplit[i].slice(1);
    }
    let newText = strSplit.join(" ");
    setText(newText);
  };

  const handleCopy = () =>{
    // var text = document.getElementById("exampleFormControlTextarea1");
    text.select();
    navigator.clipboard.writeText(text);
  }

  const handleOnChange = (event) => {
    console.log("On Change");
    setText(event.target.value);
  };
  const [text, setText] = useState("Enter text here");
  return (
    <>
      <div className="container">
        <h1>{props.heading}</h1>
        <div className="mb-3">
          <textarea
            className="form-control"
            value={text}
            onChange={handleOnChange}
            id="exampleFormControlTextarea1"
            rows="8"
          ></textarea>
        </div>
        {/* <button className="btn btn-primary mx-1" onClick={handleClearclick}>
          Clear
        </button> */}
        <button className="btn btn-primary mx-1" onClick={handleUpclick}>
          Upper Case
        </button>
        <button className="btn btn-primary mx-1" onClick={handleLowclick}>
          Lower Case
        </button>
        <button className="btn btn-primary mx-1" onClick={handleSentenseclick}>
          Sentence Case
        </button>
        <button className="btn btn-primary mx-1" onClick={handleTitleclick}>
          Title Case
        </button>
      </div>
      <div className="container my-3">
        <h1>Your Text Summary</h1>
        <p>
          {text.split(" ").length} Words and {text.length} Characters
        </p>
        <p>{0.008 * text.split(" ").length} Minutes required read the text</p>
        <h2>Preview</h2>
        <p>{text}</p>
      </div>
    </>
  );
}
